import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/data')
      .then(response => response.json())
      .then(data => setData(data));
  }, []);

  return (
    <div className="wrapper">
      <div className="container">
        <div>
          <h1 className="title">React in the Front</h1>
          {data && <p className="flask-message1">{data.message}</p>}
        </div>
      </div>
      {data && <p className="flask-message2"><a href={data.link.href} alt="GitHub Profile" target="_blank" rel= "noopener noreferrer">{data.link.text}</a></p>}
      <p>This is a simple template for creating a web app using a React front-end and Flask back-end</p>
      <p>The .bat file included in the "shortcuts" directory will create a virtual environment, install the necessary dependencies, launch the Flask server, launch the React app, and then disable the virtual environment to prevent accidental modifications</p>
      <p>Created by: Matthew Ford, CEO <a href="https://symbiotic.love" alt="Website link" target="_blank" rel= "noopener noreferrer">Symbiotic Love</a></p>
    </div>
  );
}

export default App;
